
import { localize } from '../../nls.js';
import { ParseErrorCode } from './json.js';

function getParseErrorMessage(errorCode) {
    switch (errorCode) {
        case ParseErrorCode.InvalidSymbol: return localize(111, 'Invalid symbol');
        case ParseErrorCode.InvalidNumberFormat: return localize(112, 'Invalid number format');
        case ParseErrorCode.PropertyNameExpected: return localize(113, 'Property name expected');
        case ParseErrorCode.ValueExpected: return localize(114, 'Value expected');
        case ParseErrorCode.ColonExpected: return localize(115, 'Colon expected');
        case ParseErrorCode.CommaExpected: return localize(116, 'Comma expected');
        case ParseErrorCode.CloseBraceExpected: return localize(117, 'Closing brace expected');
        case ParseErrorCode.CloseBracketExpected: return localize(118, 'Closing bracket expected');
        case ParseErrorCode.EndOfFileExpected: return localize(119, 'End of file expected');
        default:
            return '';
    }
}

export { getParseErrorMessage };
