const defaultCommonlyUsedSettings = [
    'editor.fontSize',
    'editor.formatOnSave',
    'files.autoSave',
    'editor.defaultFormatter',
    'editor.fontFamily',
    'editor.wordWrap',
    'files.exclude',
    'workbench.colorTheme',
    'editor.tabSize',
    'editor.mouseWheelZoom',
    'editor.formatOnPaste'
];
function getCommonlyUsedData(settingGroups, commonlyUsed = defaultCommonlyUsedSettings) {
    const allSettings = ( new Map());
    for (const group of settingGroups) {
        for (const section of group.sections) {
            for (const s of section.settings) {
                allSettings.set(s.key, s);
            }
        }
    }
    const settings = [];
    for (const id of commonlyUsed) {
        const setting = allSettings.get(id);
        if (setting) {
            settings.push(setting);
        }
    }
    return {
        id: 'commonlyUsed',
        label: ( localize(10021, "Commonly Used")),
        settings
    };
}
